class ToolBox {

	constructor() {
		this.room = window.room;

		this.components = [
			{
				applianceName: 'clicker',
				icon: 'data:image/svg+xml;base64,',
				iconActive: 'data:image/svg+xml;base64,'
			},
			{
				applianceName: 'selector',
				icon: 'data:image/svg+xml;base64,',
				iconActive: 'data:image/svg+xml;base64,'
			},

			{
				applianceName: 'pencil',
				icon: 'data:image/svg+xml;base64,',
				iconActive: 'data:image/svg+xml;base64,'
			},

			{
				applianceName: 'text',
				icon: 'data:image/svg+xml;base64,',
				iconActive: 'data:image/svg+xml;base64,'
			},

			{
				applianceName: 'eraser',
				icon: 'data:image/svg+xml;base64,',
				iconActive: 'data:image/svg+xml;base64,'
			},

			{
				applianceName: 'arrow',
				icon: 'data:image/svg+xml;base64,',
				iconActive: 'data:image/svg+xml;base64,'
			},

			{
				applianceName: 'laserPointer',
				icon: 'data:image/svg+xml;base64,',
				iconActive: 'data:image/svg+xml;base64,'
			},

			{
				applianceName: 'hand',
				icon: 'data:image/svg+xml;base64,',
				iconActive: 'data:image/svg+xml;base64,'
			},

			{
				applianceName: 'clear',
				icon: 'data:image/svg+xml;base64,',
				iconActive: 'data:image/svg+xml;base64,'
			},

			{
				applianceName: 'upload',
				icon: 'data:image/svg+xml;base64,',
				iconActive: 'data:image/svg+xml;base64,'
			},

			{
				applianceName: 'subscript',
				icon: 'data:image/svg+xml;base64,',
				iconActive: 'data:image/svg+xml;base64,'
			}
		];
	}

	clickAppliance(applianceName) {
		alert(applianceName);
	}

	renderButton(component) {
		const roomState = this.room.state;
		const currentApplianceName = roomState.memberState.currentApplianceName;
		const applianceName = component.applianceName;
		const isSelected = currentApplianceName === applianceName;
		const iconUrl = isSelected ? component.iconActive : component.icon;

		let button = '';
		button += '    <div class="tool-box-cell-box-left">';
		button += '        <div class="tool-box-cell">';
		button += '            <img src="' + iconUrl + '" alt="' + applianceName + '" >';
		button += '        </div>';
		button += '    </div>';

		return button;
	}

	renderNodes() {
		let nodes = '';

		this.components.forEach((component, i) => {
			nodes += this.renderButton(component);
		});

		return nodes;
	}

	render() {
		const parent = document.querySelector('.tool-box-out');
		let html = '';
		html += '<div class="tool-mid-box-left">';
		html += this.renderNodes();
		html += '</div>';

		parent.innerHTML = html;
	}
}

export { ToolBox };